mapper.select:
SELECT a.*, (SELECT name FROM tags t WHERE t.id = a.group_id) AS group_name 
FROM   Tags a 
WHERE  a.id = #{id}

mapper.update:
UPDATE Tags 
SET    name = #{name}, group_id = #{group_id} 
WHERE  id = #{id}

mapper.select:
SELECT count(tag_id)
FROM   usertags
WHERE  tag_id = #{tagId}

mapper.delete:
DELETE UserTags 
WHERE  tag_id = #{tag_id}

	AND user_id = #{user_id}

